                             (0x1 << 1) | (0x1 << 3) |
                             (mcast ? ((0x1 << 8) | (0x1 << 5)) : 0x0) |
                             (posted ? 0x0 : (0x1 << 4));

    uint32_t be32 = be;
    uint32_t be_shift = (dest_addr & ((256 / 8) - 1));

